import { useQuery } from '@apollo/client'
import Layout from '../components/MyLayout'
import { GET_ABSENCES } from './resource'

export default () => {
  const { loading, error, data } = useQuery(GET_ABSENCES, {
    id: 'davy.hausser',
  })
  if (loading) return <p>Loading</p>
  if (error) {
    return (
      <p>
        Error:
        {' '}
        {error.message}
      </p>
    )
  }
  return (
    <Layout>
      <h1>Absences</h1>
      <ul>
        {data.absences.map(({ key, date }) => <li key={key}>{`${date}`}</li>)}
      </ul>
    </Layout>
  )
}
