import { useQuery, gql } from '@apollo/client'
import Layout from '../components/MyLayout'

const RESOURCES = gql`
{
  resources {
    key
    name
    team
  }
}
`

export default () => {
  const { loading, error, data } = useQuery(RESOURCES)
  if (loading) return <p>Loading</p>
  if (error) {
    return (
      <p>
        Error:
        {' '}
        {error.message}
      </p>
    )
  }
  console.log(data)
  return (
    <Layout>
      <p>Resources are loaded</p>
      <ul>
        {data.resources.map(({ key, name, team }) => <li key={key}>{`${name} - ${team}`}</li>)}
      </ul>
    </Layout>
  )
}
