import React, { useState } from 'react'
import { ScrollSync, ScrollSyncPane } from 'react-scroll-sync'
import PropTypes from 'prop-types'
import styled from 'styled-components'
import EmojiCustomIcon from '@atlaskit/icon/glyph/emoji/custom'
import months from './SampleData'
// import './styles.css'

export default function Timeline({ epics }) {
  const [issues, setIssues] = useState(epics)

  const createEpic = () => {
    const { length } = issues
    const num = length + 1
    const issue = {
      id: `${num}`,
      key: `ST-${num}`,
      fields: {
        summary: `Epic Number ${num}`,
      },
    }
    setIssues([...issues, issue])
  }

  return (
    <Wrapper>
      <ScrollSync>
        <Wrapper2>
          <HeadlineContainer
            data-test-id="roadmap.common.components.table.components.list.container"
            width="320"
            style={{ width: '320px' }}
          >
            <EpicTitleWrapper>
              <EpicTitle>Epic</EpicTitle>
              <EpicTitleBottom />
            </EpicTitleWrapper>
            <HeaderRowWrapper>
              <div style={{ display: 'flex', overflow: 'hidden' }}>
                <ScrollSyncPane>
                  <HeaderRowScrollBar data-test-id="roadmap.common.components.table.components.list.hidden-scrollbar">
                    <HeaderRowScrollBarWidth
                      width="318"
                      style={{ width: '318px' }}
                    >
                      {issues.map((epic, i) => (
                        <RowContainer
                          key={epic.key}
                          data-test-id="roadmap.common.components.table.components.list-item.base.container"
                          draggable="true"
                        >
                          {i > 0 && (
                            <InlineCreateWrapper
                              data-test-id={`roadmap.common.components.table.components.list-item.base.inline-create-button-${i}`}
                              role="button"
                              aria-label="Inline issue create"
                              tabIndex="0"
                            >
                              <InlineCreateButton />
                            </InlineCreateWrapper>
                          )}
                          <HeadlineRowContentWrapper
                            data-test-id={`roadmap.common.components.table.components.list-item.base.content-container-${i}`}
                          >
                            <HeadlineRowContent>
                              <EpicIcon
                                src="https://solarsystem.atlassian.net/secure/viewavatar?size=medium&avatarId=10307&avatarType=issuetype"
                                alt="Epic issue type"
                              />
                              <StyledParagraph>
                                {epic.fields.summary}
                              </StyledParagraph>
                            </HeadlineRowContent>
                          </HeadlineRowContentWrapper>
                        </RowContainer>
                      ))}
                      <CreateButtonWrapper>
                        <CreateButton
                          data-test-id="roadmap.common.components.table.components.list.items.create-item.button"
                          role="button"
                          aria-label="Create issue last"
                          onClick={createEpic}
                          onKeyUp={createEpic}
                          tabIndex="0"
                        >
                          <EmojiCustomIcon className="icon" />
                        </CreateButton>
                      </CreateButtonWrapper>
                      <div
                        style={{
                          height: '1px',
                          backgroundColor: 'rgb(193, 199, 208)',
                        }}
                      />
                    </HeaderRowScrollBarWidth>
                  </HeaderRowScrollBar>
                </ScrollSyncPane>
              </div>
              <HeaderRowBorder width="318" style={{ width: '318px' }} />
            </HeaderRowWrapper>
          </HeadlineContainer>
          <TimelineContainer>
            <TimelineHeaderContainer>
              <ScrollSyncPane>
                <TimelineHeaderWrapper>
                  <TimelineHeaderHorizontal style={{ width: '9579.5px' }}>
                    <TimelineHeaderBox>
                      {months.map((month, i) => (
                        <StyledHeader
                          key={month}
                          style={{
                            left: `calc(${i} * 2.702702702702703%)`,
                            right: `calc(100% - 2.702702702702703% * ${i + 1})`,
                          }}
                        >
                          <StyledSmall>{month}</StyledSmall>
                        </StyledHeader>
                      ))}
                    </TimelineHeaderBox>
                  </TimelineHeaderHorizontal>
                </TimelineHeaderWrapper>
              </ScrollSyncPane>
            </TimelineHeaderContainer>
            <TimelineWrapper>
              <ScrollSyncPane>
                <TimelineBox id="sr-timeline">
                  <HorizontalCalendar style={{ width: '9579.5px' }}>
                    <VerticalDivider>
                      {months.map((month, i) => (
                        <StyledColumn
                          key={month}
                          style={{
                            left: `calc(${i} * 2.702702702702703%)`,
                            right: `calc(100% - 2.702702702702703% * ${i + 1})`,
                          }}
                        />
                      ))}
                    </VerticalDivider>
                    <TimelineBox1 />
                    {issues.map((epic) => (
                      <RowContainer key={epic.key}>
                        {/* <BoxWrapper style={{ left: '2%', right: '94%' }}> */}
                        <Box
                          className="box"
                          style={{ left: '2.7%', right: '94.6%' }}
                          onDragStart={() => {}}
                          onDragEnd={() => {}}
                          onValueChange={{ x: () => {} }}
                          onMouseOver={() => {}}
                          onFocus={() => {}}
                        >
                          <EpicDragLeft orientation="left">
                            <EpicDrag />
                          </EpicDragLeft>
                          <EpicDragRight orientation="right">
                            <EpicDrag />
                          </EpicDragRight>
                        </Box>
                        {/* </BoxWrapper> */}
                      </RowContainer>
                    ))}
                    <TimelineBox2 />
                    <div
                      style={{
                        height: '1px',
                        backgroundColor: 'rgb(193, 199, 208)',
                      }}
                    />
                  </HorizontalCalendar>
                </TimelineBox>
              </ScrollSyncPane>
            </TimelineWrapper>
            {/* <TimelineBottom1 /> */}
            <TimelineBottom2 />
          </TimelineContainer>
          <TimelineBottom3 />
          <TimelineBottom4>
            <TimelineBottom5 />
          </TimelineBottom4>
          <InlineCreate1>
            <InlineCreate2>
              <InlineCreate3 />
              <InlineCreate4>
                <InlineCreate5>
                  <EmojiCustomIcon className="icon" />
                </InlineCreate5>
              </InlineCreate4>
            </InlineCreate2>
          </InlineCreate1>
        </Wrapper2>
      </ScrollSync>
    </Wrapper>
    // </Container>
  )
}

Timeline.defaultProps = {
  epics: [],
}

Timeline.propTypes = {
  epics: PropTypes.arrayOf(PropTypes.objectOf),
}

