import { useQuery, gql } from '@apollo/client'
import Select from 'react-select'
import Layout from '../components/MyLayout'

export const GET_ABSENCES = gql`
  query getAbsences($id: ID!) {
    absences(id: $id) {
      date
    }
  }
`

const options = [
  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' },
]

export default () => {
  const { loading, error, data } = useQuery(GET_ABSENCES, { variables: { id: 'davy.hausser' } })
  if (loading) return <p>Loading</p>
  if (error) {
    return (
      <p>
        Error:
        {' '}
        {error.message}
      </p>
    )
  }
  return (
    <Layout>
      <h1>Absences</h1>
      <Select options={options} label="Single select" />
      <ul>
        {data.absences.map(({ date }) => <li key={date}>{`${date}`}</li>)}
      </ul>
    </Layout>
  )
}
