import Link from 'next/link'
import { useQuery, gql } from '@apollo/client'
import { port } from '../apolloClient'
import Layout from '../components/MyLayout'

const GET_TOKEN = gql`
  query getToken {
    login
  }
`

export default () => {
  const { data } = useQuery(GET_TOKEN)

  if (typeof localStorage !== 'undefined' && data && data.login) {
    localStorage.setItem('token', data.login)
  }

  return (
    <Layout>
      <p className="description">
        {data && data.login
          ? <code>{`You are logged in session with token: ${data.login}`}</code>
          : <a href={`http://localhost:${port}/auth/provider`}>Login</a>}
      </p>

      <div className="grid">
        <Link href="/roadmap">
          <a href="/roadmap" className="card">
            <h3>Roadmap</h3>
            <p>Learn about all the roadmap on the project</p>
          </a>
        </Link>
        <Link href="/dashboard">
          <a href="/dashboard" className="card">
            <h3>Dashboard</h3>
            <p>Learn about all the dashboard on the project</p>
          </a>
        </Link>
        <Link href="/resources">
          <a href="/resources" className="card">
            <h3>Resources</h3>
            <p>Learn about all the resources on the project</p>
          </a>
        </Link>
        <Link href="/issues">
          <a href="/issues" className="card">
            <h3>Issues</h3>
            <p>Learn about all the issues on the project</p>
          </a>
        </Link>
      </div>

      <style jsx>
        {`
              .description {
                line-height: 1.5;
                font-size: 1.5rem;
              }

              code {
                background: #fafafa;
                border-radius: 5px;
                padding: 0.75rem;
                font-size: 1.1rem;
                font-family: Menlo, Monaco, Lucida Console, Liberation Mono,
                  DejaVu Sans Mono, Bitstream Vera Sans Mono, Courier New, monospace;
              }

              .grid {
                display: flex;
                align-items: center;
                justify-content: center;
                flex-wrap: wrap;

                max-width: 800px;
                margin-top: 3rem;
              }

              .card {
                margin: 1rem;
                flex-basis: 45%;
                padding: 1.5rem;
                text-align: left;
                color: inherit;
                text-decoration: none;
                border: 1px solid #eaeaea;
                border-radius: 10px;
                transition: color 0.15s ease, border-color 0.15s ease;
              }

              .card:hover,
              .card:focus,
              .card:active {
                color: #0070f3;
                border-color: #0070f3;
              }

              .card h3 {
                margin: 0 0 1rem 0;
                font-size: 1.5rem;
              }

              .card p {
                margin: 0;
                font-size: 1.25rem;
                line-height: 1.5;
              }

              @media (max-width: 600px) {
                .grid {
                  width: 100%;
                  flex-direction: column;
                }
              }
      `}

      </style>
    </Layout>
  )
}
