import Head from 'next/head'
import Link from 'next/link'
import { useQuery, gql } from '@apollo/client'
import { port } from '../apolloClient'

const GET_TOKEN = gql`
  query getToken {
    login
  }
`

const IndexPage = () => {
  const { data } = useQuery(GET_TOKEN)

  let token = null
  if (typeof localStorage !== 'undefined' && data && data.login) {
    token = data.login
    localStorage.setItem('token', data.login)
  }
  console.log(token)

  return (
    <div className="container">
      <Head>
        <title>Next Planning App</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <main>
        <h1 className="title">
          Welcome to
          {' '}
          <a href="https://nextjs.org">Next.js!</a>
        </h1>

        <p className="description">
          Get started by editing
          {' '}
          <code>pages/index.js</code>
        </p>

        <div className="grid">
          <Link href="/resources">
            <a href="/resources" className="card">
              <h3>Resources</h3>
              <p>Learn about all the resources on the project</p>
            </a>
          </Link>
          <Link href="/issues">
            <a href="/issues" className="card">
              <h3>Issues</h3>
              <p>Learn about all the issues on the project</p>
            </a>
          </Link>
        </div>
        {data && data.login
          ? (
            <>
              <p>{`You are logged in session with token: ${data.login}`}</p>

            </>
          )
          : <a href={`http://localhost:${port}/auth/provider`}>Login</a>}
      </main>

      <footer>
        <a
          href="https://zeit.co?utm_source=create-next-app&utm_medium=default-template&utm_campaign=create-next-app"
          target="_blank"
          rel="noopener noreferrer"
        >
          Powered by
          {' '}
          <img src="/zeit.svg" alt="ZEIT Logo" />
        </a>
      </footer>

      <style jsx>
        {`
      .container {
        min-height: 100vh;
        padding: 0 0.5rem;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
      }

      main {
        padding: 5rem 0;
        flex: 1;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
      }

      footer {
        width: 100%;
        height: 100px;
        border-top: 1px solid #eaeaea;
        display: flex;
        justify-content: center;
        align-items: center;
      }

      footer img {
        margin-left: 0.5rem;
      }

      footer a {
        display: flex;
        justify-content: center;
        align-items: center;
      }

      a {
        color: inherit;
        text-decoration: none;
      }

      .title a {
        color: #0070f3;
        text-decoration: none;
      }

      .title a:hover,
      .title a:focus,
      .title a:active {
        text-decoration: underline;
      }

      .title {
        margin: 0;
        line-height: 1.15;
        font-size: 4rem;
      }

      .title,
      .description {
        text-align: center;
      }

      .description {
        line-height: 1.5;
        font-size: 1.5rem;
      }

      code {
        background: #fafafa;
        border-radius: 5px;
        padding: 0.75rem;
        font-size: 1.1rem;
        font-family: Menlo, Monaco, Lucida Console, Liberation Mono,
          DejaVu Sans Mono, Bitstream Vera Sans Mono, Courier New, monospace;
      }

      .grid {
        display: flex;
        align-items: center;
        justify-content: center;
        flex-wrap: wrap;

        max-width: 800px;
        margin-top: 3rem;
      }

      .card {
        margin: 1rem;
        flex-basis: 45%;
        padding: 1.5rem;
        text-align: left;
        color: inherit;
        text-decoration: none;
        border: 1px solid #eaeaea;
        border-radius: 10px;
        transition: color 0.15s ease, border-color 0.15s ease;
      }

      .card:hover,
      .card:focus,
      .card:active {
        color: #0070f3;
        border-color: #0070f3;
      }

      .card h3 {
        margin: 0 0 1rem 0;
        font-size: 1.5rem;
      }

      .card p {
        margin: 0;
        font-size: 1.25rem;
        line-height: 1.5;
      }

      @media (max-width: 600px) {
        .grid {
          width: 100%;
          flex-direction: column;
        }
      }
    `}
      </style>

      <style jsx global>
        {`
      html,
      body {
        padding: 0;
        margin: 0;
        font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Oxygen,
          Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue, sans-serif;
      }

      * {
        box-sizing: border-box;
      }
    `}
      </style>
    </div>
  )
}

export default IndexPage
