import { useState } from 'react'
import { useQuery, gql } from '@apollo/client'
import Select from 'react-select'
import Layout from '../components/MyLayout'
import { GET_RESOURCES } from './resources'

const GET_ABSENCES = gql`
  query getAbsences($id: ID!) {
    absences(id: $id) {
      date
    }
  }
`

export default () => {
  // const [resource, setResource] = useState({ key: 'davy.hausser', name: 'Davy Hausser' })
  const [resource, setResource] = useState('davy.hausser')
  let options
  const { loading, error, data } = useQuery(GET_ABSENCES, { variables: { id: resource } })
  const { loading: resourcesLoading, error: resourcesError, data: resourcesData } = useQuery(GET_RESOURCES)
  if (loading || resourcesLoading) return <p>Loading</p>
  if (error || resourcesError) {
    return (
      <p>
        Error:
        {' '}
        {error.message || resourcesError.message}
      </p>
    )
  }
  if (resourcesData && resourcesData.resources) {
    options = resourcesData.resources.map(({ value, label }) => ({ key: value, name: label }))
  }
  return (
    <Layout>
      <h1>Absences</h1>
      <p>{resource}</p>
      {/* <Select options={options} onChange={({ value, label }) => console.log({ key: value, name: label })} /> */}
      {/* <Select options={options} onChange={({ value, label }) => setResource({ key: value, name: label })} /> */}
      <ul>
        {data.absences.map(({ date }) => <li key={date}>{`${date}`}</li>)}
      </ul>
    </Layout>
  )
}
