import { useQuery, gql } from '@apollo/client'

const RESOURCES = gql`
{
  resources {
    key
    name
    team
  }
}
`

export default () => {
  const { loading, error, data } = useQuery(RESOURCES)
  if (loading) return <p>Loading</p>
  if (error) {
    return (
      <p>
        Error:
        {' '}
        {error.message}
      </p>
    )
  }
  console.log(data)
  return <p>Resources are loaded</p>
}
