import { useState } from 'react'
import { useQuery, gql } from '@apollo/client'
import Select from 'react-select'
import Layout from '../components/MyLayout'
import { GET_RESOURCES } from './resources'

const GET_ABSENCES = gql`
  query getAbsences($id: ID!) {
    absences(id: $id) {
      date
    }
  }
`

const options = [
  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' },
]

export default () => {
  const { loading, error, data } = useQuery(GET_ABSENCES, { variables: { id: 'davy.hausser' } })
  const { loading: resourcesLoading, error: resourcesError, data: resourcesData } = useQuery(GET_RESOURCES)
  if (loading || resourcesLoading) return <p>Loading</p>
  if (error || resourcesError) {
    return (
      <p>
        Error:
        {' '}
        {error.message || resourcesError.message}
      </p>
    )
  }
  if (resourcesData && resourcesData.resources) {
    const options = resourcesData.resources.map(({ key, name }) => ({ value: key, label: name }))
    console.log(options)
  }
  return (
    <Layout>
      <h1>Absences</h1>
      <Select options={options} label="Single select" styles={{ width: '10em' }} />
      <ul>
        {data.absences.map(({ date }) => <li key={date}>{`${date}`}</li>)}
      </ul>
    </Layout>
  )
}
